package Lopps;

public class forAndWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("-----for loop-------");
		int i=1;
		for(i=1;i<=10;i++) {
			System.out.println(i);
			
		}
		System.out.println("-----whileloop-------");
		int j=10;
		while(j>=1) {
			System.out.println(j);
			j--;
		}

	}
	

}
